INSERT INTO Textarticle (ID, ChangedDateTime, CreatedDateTime, CreatedBy, ChangedBy, NavigationID, SubnavID, TextarticleGroup, Head, Subhead, Excerpt, Text, active, Date, Picture) VALUES (1,1017140344.140000,1011102114.300000,1,1,0,0,0,'1[SHOP] - Branchenlösung für Immobilienkaufleute','Willkommen im Testsystem',NULL,'<P><FONT face=Arial,Geneva,Sans-Serif></FONT>&nbsp;</P>\r\n<P><FONT color=#00008b face=Courier,Monospace size=5><STRONG>Das Wichtigste zuerst:</STRONG></FONT></P>\r\n<UL>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Schauen Sie sich in Ruhe um und probieren Sie die angebotenen Menüpunkte und Adminstrationselemente einfach per Klick aus.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Falscheingaben, egal ob Menüs, Texte, Bilder, Grafiken oder diese Mustereinträge, können Sie jederzeit mit einem Klick auf den Menüpunkt - <FONT color=#00008b face=Courier,Monospace><STRONG>ADMINISTRATION</STRONG></FONT>&nbsp;- <ADMINISTRATION>und den dort&nbsp;angebotenen Optionen bearbeiten, freistellen, aktivieren/inaktivieren, zuordnen oder löschen.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Sämtliche Eingaben werden in Ihrer Datenbank gespeichert und stehen Ihnen für eine sofortige oder spätere Verarbeitung zur Verfügung.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Eine Bedienungsanleitung steht Ihnen jederzeit mit einem Klick auf den Menüpunkt -&nbsp;<FONT color=#00008b face=Courier,Monospace><STRONG>ONLINE-HILFE </STRONG></FONT>&nbsp;- <ONLINE-HILFE>zur Verfügung.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Das Testsystem ist standardmäßig für den öffentlichen Zugriff \"geschlossen\". D.h. Sie müssen zunächst das System mit einem Klick auf den Menüpunkt - <FONT color=#00008b face=Courier,Monospace><STRONG>ADMINISTRATION</STRONG></FONT>&nbsp;- und über den Punkt SICHERHEIT -&nbsp;Web offnen/schliessen - freigeben. Bis zur Ausführung dieser Aktion wird bei Au