INSERT INTO Textarticle VALUES (1, '1016287665.630000', '0.000000', 1, 1, 0, 0, 0, '1[VERS] - Branchenlösung für Versicherungskaufleute', 'Willkommen im Testsystem', NULL, '<P><FONT face=Arial,Geneva,Sans-Serif></FONT>&nbsp;</P>\r\n<P><FONT face=Courier,Monospace color=#00008b size=5><STRONG>Das Wichtigste zuerst:</STRONG></FONT></P>\r\n<UL>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Schauen Sie sich in Ruhe um und probieren Sie die angebotenen Menüpunkte und Adminstrationselemente einfach per Klick aus.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Falscheingaben, egal ob Menüs, Texte, Bilder, Grafiken oder diese Mustereinträge, können Sie jederzeit mit einem Klick auf den Menüpunkt - <FONT face=Courier,Monospace color=#00008b><STRONG>ADMINISTRATION</STRONG></FONT>&nbsp;- <ADMINISTRATION>und den dort&nbsp;angebotenen Optionen bearbeiten, freistellen, aktivieren/inaktivieren, zuordnen oder löschen.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Sämtliche Eingaben werden in Ihrer Datenbank gespeichert und stehen Ihnen für eine sofortige oder spätere Verarbeitung zur Verfügung.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Eine Bedienungsanleitung steht Ihnen jederzeit mit einem Klick auf den Menüpunkt -&nbsp;<FONT face=Courier,Monospace color=#00008b><STRONG>ONLINE-HILFE </STRONG></FONT>&nbsp;- <ONLINE-HILFE>zur Verfügung.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Das Testsystem ist standardmäßig für den öffentlichen Zugriff "geschlossen". D.h. Sie müssen zunächst das System mit einem Klick auf den Menüpunkt - <FONT face=Courier,Monospace color=#00008b><STRONG>ADMINISTRATION</STRONG></FONT>&nbsp;- und über den Punkt SICHERHEIT -&nbsp;Web offnen/schliessen - freigeben. Bis zur Ausführung dieser Aktion wird bei Aufruf der Präsenz nur eine Hinweisseite angezeigt.</FONT><BR></FONT></LI></UL>\r\n<P><FONT face=Courier,Monospace color=#00008b size=5><STRONG>Zur Orientierung:</STRONG></FONT></P>\r\n<UL>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Das System bietet Ihnen die Möglichkeit beliebige Inhalte für die Informationsbereiche <BR>INFORMATIONEN und ANGEBOTE einzufügen.</FONT> </FONT>\r\n<LI><FONT size=2><FONT face=Arial,Geneva,Sans-Serif>Über das Menü können Sie eine beliebige Navigationsstruktur für den jeweiligen Bereich aufbauen und den einzelnen Menüpunkten eine beliebige Anzahl von Dokumenten oder Angeboten hinzufügen.</FONT> </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Die Menüeinträge&nbsp;im Bereich SERVICE sind im System fest verankert und können von Ihnen nicht gelöscht, aber&nbsp;Inhaltlich erweitert und verändert werden</FONT></LI></UL>', 1, '0.0000', '1ci_klein_w.gif');
