INSERT INTO Textarticle VALUES (4, '1016291809.510000', '1016287805.530000', 1, 1, 0, 2, 0, 'Aus TEST wird ERNST', 'So erhalten Sie Ihre eigene Präsenz !', NULL, '<P><FONT face=Arial,Geneva,Sans-Serif></FONT>&nbsp;</P>\r\n<P><FONT face=Arial,Geneva,Sans-Serif>Sie haben sich entschlossen eine Präsenz unter Ihrer eigenen Domain zu eröffnen und zu betreiben ?</FONT></P>\r\n<P><FONT face=Courier,Monospace color=#00008b size=5><STRONG>Kein Problem ! Wir erledigen alles ! Versprochen !</STRONG></FONT></P>\r\n<OL><FONT face=Arial,Geneva,Sans-Serif>\r\n<LI target="_new"><FONT face=Arial,Geneva,Sans-Serif>Folgen Sie <A target=_new href="http://www.register.1ci.de/1Ci/dmerce?qTemplate=Anmeldung,anmeldung&amp;ProjectType=2&amp;ProjectID=1&amp;ResellerID=1"><STRONG>diesem Link</STRONG></A> </FONT>\r\n<LI target="_new"><FONT face=Arial>Füllen Sie die Felder aus </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif>Bestätigen Sie Ihre Eingabe</FONT></LI></OL>\r\n<P target="_new"></FONT><FONT face=Arial,Geneva,Sans-Serif>Wir werden Ihnen direkt nach der Aktivierung sämtliche erforderlichen Vertrags- und Antragsunterlagen übermitteln. Erst wenn Sie uns diese gegengezeichnet übermittelt haben beginnt das kostenpflichtige Vertragsverhältnis.</FONT></P>\r\n<P><FONT size=5><FONT face=Courier,Monospace color=#00008b><STRONG>Vollständig und&nbsp;unabhängig <BR></STRONG></FONT><FONT face=Courier,Monospace color=#800000>Soviel Sie wollen für mtl. <BR>59 EURO* inkl. MwSt.</FONT></FONT></P>\r\n<UL>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif>inkl. Domain (.de oder .com) </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif>inkl. Domaingebühren für Registrierung, Pflege und DENIC</FONT> \r\n<LI><FONT face=Arial,Geneva,Sans-Serif>inkl. 50 Emailadressen</FONT> \r\n<LI><FONT face=Arial,Geneva,Sans-Serif>inkl. 500 MB Datentransfer</FONT> \r\n<LI><FONT face=Arial>inkl. 50 MB Speicherplatz</FONT> \r\n<LI><FONT face=Arial>inkl. Layoutvorlagen</FONT></LI></UL>\r\n<P><FONT face=Arial,Geneva,Sans-Serif>Nähere Informationen und eine Übersicht zu den Leistungen finden Sie <A target=_new href="http://www.vers.demo.1ci.de/"><STRONG>hier</STRONG></A><BR><FONT face=Arial>Zu den Preisen und Konditionen wählen Sie bitte den entsprechenden Menüeintrag.</FONT></FONT></P>\r\n<P><FONT face=Arial,Geneva,Sans-Serif size=2>* zzgl. einmalige Einrichtungsgebühren von 39 EURO inkl. MwSt.</FONT></P>', 1, '0.0000', '1ci_klein_w.gif');
INSERT INTO Textarticle VALUES (5, '1016287870.150000', '1016287870.150000', 1, 1, 0, 0, 0, 'Erste Schritte im Testsystem', '1[VERS] - Die Präsenz für Versicherungskaufleute', NULL, '<FONT face=Courier color=#00008b size=5></FONT>\r\n<P><STRONG></STRONG>&nbsp;</P>\r\n<P><FONT face=Courier color=#00008b size=5><STRONG>Jetzt geht es los !</STRONG></FONT></P>\r\n<P><FONT face=Arial,Geneva,Sans-Serif color=#000000 size=3>Wir heißen Sie herzlich in <U><STRONG>Ihrem</STRONG></U> [VERS]-Testsystem willkommen.<BR>Die Ihnen vorliegenden Anwendung können Sie für eine Dauer von 14 Tagen kostenlos testen. <BR>Sollte Ihnen dieser Zeitraum nicht ausreichen, dann teilen Sie uns Ihren Verlängerungswunsch bitte rechtzeitig vor Ablauf mit.</FONT></P>\r\n<P>&nbsp;</P><FONT face=Arial color=#000000 size=3>\r\n<P><FONT face=Courier,Monospace size=4>Unsere Garantien:</FONT></P>\r\n<UL>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Sie gehen keine Verpflichtungen ein </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Es besteht kein Kaufzwang </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Die Testphase ist für Sie absolut kostenlos </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Wir löschen sämtlicher Inhalte nach 14 Tagen.automatisch und vollständig</FONT></LI></UL>\r\n<P><FONT face=Courier,Monospace size=4>Nutzungsbedingungen</FONT></P>\r\n<UL>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Die Testumgebung wird ausschließlich zu Test und Übungsszwecken zur Verfügung gestellt und steht Ihnen für eine Dauer von 14 Tagen zur Verfügung. Maßgeblich für den Beginn des Nutzungszeitraums ist das Absendedatum unseres "Accept-Mails" mit der Bekanntgabe Ihrer Zugangsdaten für dieses System an Sie. </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Das Testsystem ist nicht öffentlich und darf während der eingeräumten Nutzungsdauer nicht öffentlich betrieben werden. </FONT>\r\n<LI><FONT face=Arial,Geneva,Sans-Serif size=2>Der Nutzer stellt den Anbieter dieser Lös